#include <gtest/gtest.h>
#include "largest_number.cpp"

class LargestNumberTest: public testing::Test{
    protected:
        Solution sol;
};
TEST_F(LargestNumberTest, Cmp1){
    EXPECT_EQ(true, Solution::cmp("121", "12"));
    EXPECT_EQ(false, Solution::cmp("12", "121"));
    EXPECT_EQ(false, Solution::cmp("12", "120"));
    EXPECT_EQ(true, Solution::cmp("30", "3"));
    EXPECT_EQ(false, Solution::cmp("123", "12"));
    EXPECT_EQ(true, Solution::cmp("12", "123"));
    EXPECT_EQ(true, Solution::cmp("123", "7"));
    EXPECT_EQ(true, Solution::cmp("123", "789"));
    EXPECT_EQ(false, Solution::cmp("8308", "830"));
    EXPECT_EQ(true, Solution::cmp("830", "8308"));
}
TEST_F(LargestNumberTest, Small){
    int a[] = {3, 30, 34, 5, 9};
    VI nums(ASZ(a));
    EXPECT_EQ("9534330", sol.largestNumber(nums));
}
TEST_F(LargestNumberTest, Small2){
    int a[] = {121, 12};
    VI nums(ASZ(a));
    EXPECT_EQ("12121", sol.largestNumber(nums));
}
TEST_F(LargestNumberTest, Reverse){
    int a[] = {1,2,3,4,5,6,7,8,9,0};
    VI nums(ASZ(a));
    EXPECT_EQ("9876543210", sol.largestNumber(nums));
}
TEST_F(LargestNumberTest, DamnZero){
    int a[] = {0,0};
    VI nums(ASZ(a));
    EXPECT_EQ("0", sol.largestNumber(nums));
}
